import React from 'react';
import './App.css';
import breactfast from './component/breactfast';
import lunch from './component/lunch';
import dinner from './component/dinner';

function App() {

  class Demo extends React.Component {
    constructor() {
      super();
      this.state = {
        curentTime: new Date().toLocaleTimeString(),
      }
    }

    componentDidMount() {
      setInterval(() => {
        this.setState({
          curentTime: new Date().toLocaleTimeString()
        })
      }, 1000);
    }

    render() {

      let component;
      let time = new Date().getHours();

      if (time >= 6 && time < 12) {
        let component = <breactfast />
      } else if (time >= 12 && time < 17) {
        let component = <lunch />
      }else if(time >= 17 && time < 21){
        let component = <dinner />
    }
      return (
        <>
          <h1> {this.state.curentTime} </h1>

        </>
      )
    }
  }
  return (
    <>
      <Demo />
    </>
  );
}

export default App;
